import React, { Fragment, useState } from 'react';


function AddProduct() {

  const [items, setItems] = useState([])

  async function AddProductHamdler(movie) {
    try{
      const response = await fetch('https://react-http-6b4a6.firebaseio.com/movies.json', {
    method: 'POST',
    body: JSON.stringify(movie),
    headers: {
      'Content-Type': 'application/json'
    }
  });
  const data = await response.json();
  console.log(data);

  const newPhone = data.items.map((data) => {
    return {
      id: data.id,
      title: data.title,
      description: data.description,
      brand: data.brand
    }

    
  } )
  setItems(newPhone)
    }
    catch(error) {
      console.log(error)
    }
  
}

return ( <Fragment>
  <section>
    <form>
      <div >
        <label htmlFor='title'>Title</label>
        <input type='text' id='title'/>
      </div>
      <div >
        <label htmlFor='description'>Description</label>
        <textarea rows='5' id='description'></textarea>
      </div>
      <div >
        <label htmlFor='brand'>Brand</label>
        <input type='text' id='brand'/>
      </div>
      <button onClick={AddProductHamdler}>Add Product</button>
    </form>
    </section>
    <section>
    {items.map((data) => <ProductItems key={data.id} title={data.title} description={data.description} brand={data.brand}/>)}
    </section>
    </Fragment>
  );
}








export default AddProduct
