import { Fragment } from "react";
import Card from "../UI/Card";
import {ProductsItems} from './ProductItems'


const Products = () => {
    const [products, setProducts] = useState([]);
  
    useEffect(() => {
      const fetchProducts = async () => {
        const response = await fetch('https://dummyjson.com/products');
        const productData = await response.json();
  
        const transformedPhones = [];
  
        for (const key in productData) {
          transformedPhones.push({
            id: key,
            name: productData[key].name,
            description: productData[key].description,
            price: productData[key].price,
            brand: productData[key].brand
          });
        }
  
        setProducts(transformedPhones);
      };
  
      fetchProducts();
    }, []);
  
    const productsList = products.map((product) => (
      <ProductsItems
        key={product.id}
        id={product.id}
        name={product.name}
        description={product.description}
        price={product.price}
        brand={product.brand}
      />
    ));
  
    return (
        <Fragment>
            <section>
                <button>Get Products</button>
            </section>
      <section>
        <Card>
          <ul>{productsList}</ul>
        </Card>
      </section>
      </Fragment>
    );
  };
  
  export default Products;
