import  { useContext, useRef } from 'react';

async function AddProductHandler(product) {
  const response = await fetch('https://dummyjson.com/products/add', {
    method: 'POST',
    body: JSON.stringify(product),
    headers: {
      'Content-Type': 'application/json'
    }
  });
  const data = await response.json();
  console.log(data);

}

function AddProduct(props) {

  const [items, setItems] = useContext


  const titleRef = useRef('');
  const descriptionRef = useRef('');
  const brandRef = useRef('');

  function submitHandler(event) {
    event.preventDefault();

    
    const product = {
      title: titleRef.title,
      description: descriptionRef.description,
      brand: brandRef.brand,
    };

    setItems(product);
  }

  return (
    <form onSubmit={submitHandler}>
      <div>
        <label htmlFor='title'>Title</label>
        <input type='text' id='title' ref={titleRef} />
      </div>
      <div >
        <label htmlFor='description'>Descrption</label>
        <textarea rows='5' id='description' ref={descriptionRef}></textarea>
      </div>
      <div>
        <label htmlFor='brand'>Release Date</label>
        <input type='text' id='brand' ref={brandRef} />
      </div>
      <button onClick={AddProductHandler}>Add Movie</button>
    </form>
  );
}

export default AddProduct;
