import { useState } from "react"
import React from "react";
import {ProductItems} from './ProductItems'

function Products() {
    const [products, setProducts] = useState([]);
    async function fetchData() { 
      try {
        const response = await fetch('https://dummyjson.com/products')
        const data = await response.json();
        console.log(data);
        

        const transformedPhone = data.products.map((item) => {
            return {
                id:item.id,
                title:item.title,
                description:item.description,
                brand: item.brand,
                images:`https://dummyjson.com/images/${item.images}`
            }
        })
        setProducts(transformedPhone)}
        catch(error) {console.log(error)}
    }
    

    return (
        <React.Fragment>
            <section>
                <button onClick={fetchData}>GET</button>
            </section>
            <section>
                {products.length > 0 && products.map((item) => <ProductItems key={item.id} title={item.title} description={item.description} brand={item.brand}/>) }
            </section>
           
        </React.Fragment>
    )
}
export default Products;
