import React, { useRef } from 'react';



async function AddProductHandler(items) {
    const response = await fetch('https://dummyjson.com/products/add', {
      method: 'POST',
      body: JSON.stringify(items),
      headers: {
        'Content-Type': 'application/json'
      }
    });
    const data = await response.json();
    console.log(data);
  }



function AddProduct(props) {
  const titleRef = useRef('');
  const descriptionRef = useRef('');
  const brandRef = useRef('');

  function submitHandler(event) {
    event.preventDefault();


    const products = {
      title: titleRef.current.value,
      openingText: descriptionRef.current.value,
      releaseDate: brandRef.current.value,
    };

    props.onAddProduct(products);
  }

  return (
    <form onSubmit={submitHandler}>
      <div>
        <label htmlFor='title'>Title</label>
        <input type='text' id='title' ref={titleRef} />
      </div>
      <div>
        <label htmlFor='description'>Description</label>
        <textarea rows='5' id='description' ref={descriptionRef}></textarea>
      </div>
      <div>
        <label htmlFor='brand'>Brand</label>
        <input type='text' id='brand' ref={brandRef} />
      </div>
      <button>Add Product</button>
    </form>
  );
}

export default AddProduct;
